void beforePacks() {
  FileWriter file = getFile("beforepacks-bsh.txt");
  file.write("beforepacks");
  file.close();
}

void afterPacks() {
  FileWriter file = getFile("afterpacks-bsh.txt");
  file.write("afterpacks");
  file.close();
}

