void beforeDeletion() {
  print("(beanshell) before deletion '" + files + "'");
  print("  INSTALL_PATH = '" + variables.getProperty("INSTALL_PATH") + "'");
}

void afterDeletion() {
  print("(beanshell) after deletion '" + files + "'");
  print("  INSTALL_PATH = '" + variables.getProperty("INSTALL_PATH") + "'");
}

FileWriter getFile(String name) {
  File parent = new File(installData.getVariable("INSTALL_PATH"));
  return new FileWriter(new File(parent, name));
}
